timeSeriesQuery.timeSeriesQueryLanguage:
fetch k8s_container
| metric 'kubernetes.io/container/cpu/request_cores'
| ${project_id}
| ${location}
| ${cluster_name}
| ${namespace_name}
| ${top_level_controller_name}
| ${top_level_controller_type}
| filter resource.namespace_name !~ '(kube|istio|gatekeeper|gke|gmp)-system'
| group_by
    [resource.project_id, resource.location, resource.cluster_name,
     resource.namespace_name,
     metadata.system.top_level_controller_name:
       metadata.system_labels.top_level_controller_name],
    [value_core_request_mean: mean(value.request_cores)]
| group_by [], [row_count: row_count()]

{ all_non_besteffort_workloads:
    fetch k8s_container
    | metric 'kubernetes.io/container/memory/request_bytes'
| ${project_id}
| ${location}
| ${cluster_name}
| ${namespace_name}
| ${top_level_controller_name}
| ${top_level_controller_type}
    | filter resource.namespace_name !~ '(kube|istio|gatekeeper|gke|gmp)-system'
    | group_by 5m, [value_request_bytes_mean: mean(value.request_bytes)]
    | every 5m
    | group_by
    [resource.project_id, resource.location, resource.cluster_name,
     resource.namespace_name,
     metadata.system.top_level_controller_name:
       metadata.system_labels.top_level_controller_name],
    [value_request_bytes_mean_mean: mean(value_request_bytes_mean)]
        | filter value_request_bytes_mean_mean != 0 
        | group_by [], [row_count: row_count()]
; burstable_workload_risk:
    fetch k8s_container
    | metric 'kubernetes.io/container/memory/request_utilization'
| ${project_id}
| ${location}
| ${cluster_name}
| ${namespace_name}
| ${top_level_controller_name}
| ${top_level_controller_type}
    | filter (resource.namespace_name !~ '(kube|istio|gatekeeper|gke|gmp)-system'
         && metric.memory_type == 'non-evictable')
    | group_by 3d, [value_request_utilization_mean: mean(value.request_utilization)]
| every 5m
    | group_by
        [resource.project_id, resource.location, resource.cluster_name,
         resource.namespace_name, metadata.system_labels.top_level_controller_name, metadata.system_labels.top_level_controller_type],
        [value_request_utilization_mean: mean(value_request_utilization_mean)]
    | filter value_request_utilization_mean >= 1
    | group_by [], [row_count: row_count()] }
| join
| value [v_0: scale(div(burstable_workload_risk.row_count, all_non_besteffort_workloads.row_count),'%')]

